UPDATE sys_task
project_id = #{projectId}, parent_task_id = #{parentTaskId}, title = #{title}, description = #{description}, status = #{status}, priority = #{priority}, assignee_id = #{assigneeId}, start_date = #{startDate}, end_date = #{endDate}, estimated_hours = #{estimatedHours}, actual_hours = #{actualHours}, progress = #{progress}, original_end_date = #{originalEndDate}, delayed_days = #{delayedDays}, delay_reason = #{delayReason}, delay_count = #{delayCount}, last_delay_date = #{lastDelayDate}, is_delayed = #{isDelayed}, updated_at = #{updatedAt}
WHERE id = #{id}
